func Test_DecodeCRL(t *testing.T) {
	data, err := base64.StdEncoding.DecodeString(crlEmptyBase64)
	if err != nil {
		t.Error(err)
	}

	emptyList, err := DecodeRawTBSCertList(data)
	if err != nil {
		t.Error(err)
	}

	if len(emptyList.RevokedCertificates) > 0 {
		t.Error("Expected an empty list.")
	}

	data, err = base64.StdEncoding.DecodeString(crlFilledBase64)
	if err != nil {
		t.Error(err)
	}

	filledList, err := DecodeRawTBSCertList(data)
	if err != nil {
		t.Error(err)
	}

	if len(filledList.RevokedCertificates) != 220 {
		t.Errorf("Expected 220 entries, got %d.", len(filledList.RevokedCertificates))
	}

	expectedSerial := NewSerialFromHex("0101ea518c68c0f00789e9cd92736c75")
	actualSerial := NewSerialFromBytes(filledList.RevokedCertificates[0].SerialNumber.Bytes)
	if expectedSerial.BinaryString() != actualSerial.BinaryString() {
		t.Errorf("Expected %s, but got %s", expectedSerial, actualSerial)
	}
}

func TestDetectPrecertSigningEKU(t *testing.T) {
	b, _ := pem.Decode([]byte(kCtPrecertificateSigningEku))

	cert, err := x509.ParseCertificate(b.Bytes)
	if err != nil {
		t.Error(err)
	}

	if !IsPreIssuer(cert) {
		t.Errorf("Failed to identify preissuer")
	}

	b, _ = pem.Decode([]byte(kNoCtPrecertificateSigningEku))

	cert, err = x509.ParseCertificate(b.Bytes)
	if err != nil {
		t.Error(err)
	}

	if IsPreIssuer(cert) {
		t.Errorf("Bad result from IsPreIssuer")
	}
}

